DROP TABLE IF EXISTS `mc_channel`;
CREATE TABLE mc_channel(	cid int(11) unsigned NOT NULL AUTO_INCREMENT,	title varchar(200) NOT NULL,	total_clips int NOT NULL,	PRIMARY KEY (`cid`));
DROP TABLE IF EXISTS `mc_users`;
CREATE TABLE mc_users(	uid int(11) unsigned NOT NULL AUTO_INCREMENT,	username varchar(32) NOT NULL,	password varchar(32) NOT NULL,	fullname varchar(32) NULL,	address varchar(64) NULL,	phone varchar(20) NULL,	mobile varchar(20) NULL,	email varchar(32) NULL,	sex tinyint NULL,	date_joined datetime NULL,	question varchar(200) NULL,	answer varchar(100) NULL,	allow_acp tinyint NULL,	PRIMARY KEY (`uid`));
DROP TABLE IF EXISTS `mc_clips`;
CREATE TABLE mc_clips(	id int(11) unsigned NOT NULL AUTO_INCREMENT,	title varchar(200) NOT NULL,	description varchar(1000) NULL,	chanel_id int NOT NULL,	path varchar(200) NOT NULL,	submiter int NOT NULL,	date_added datetime NOT NULL,	last_modified datetime NOT NULL,	image varchar(200) NULL,	hits bigint NULL,	private tinyint NULL,	approve tinyint NOT NULL,	PRIMARY KEY (`id`));
DROP TABLE IF EXISTS `mc_suggestion`;
CREATE TABLE mc_suggestion(	id int(11) unsigned NOT NULL AUTO_INCREMENT,	user_id int NOT NULL,	date_added datetime NOT NULL,	title varchar(200) NOT NULL,	content text NOT NULL,	PRIMARY KEY (`id`));
DROP TABLE IF EXISTS `mc_report`;
CREATE TABLE mc_report(	id int(11) unsigned NOT NULL AUTO_INCREMENT,	clip_id int NOT NULL,	user_id int NOT NULL,	comment varchar(500) NULL,	report_date date NOT NULL,PRIMARY KEY (`id`));
DROP TABLE IF EXISTS `mc_comments`;
CREATE TABLE mc_comments(	id int(11) unsigned NOT NULL AUTO_INCREMENT,	clip_id int NULL,	user_id int NULL,	title varchar(100) NULL,	comment text NULL,	approve tinyint(1) NOT NULL,	date_added datetime NULL,	PRIMARY KEY (`id`));
DROP TABLE IF EXISTS `mc_clip_rate`;
CREATE TABLE mc_clip_rate(	clip_id int NOT NULL,	user_id int NOT NULL,	rate int NOT NULL);
